func (d *diskQueue) readOne() ([]byte, error) { var err error var msgSize int32 // 当前读取文件是否打开，没有则打开当前文件 if d.readFile == nil { // 打开读取的文件（也就是当前队列头所在的文件），如果文件大小到达上线maxBytesPerFile，readFileNum加一 curFileName := d.fileName(d.readFileNum) d.readFile, err = os.OpenFile(curFileName, os.O_RDONLY, 0600) if err != nil { return nil, err } d.logf(INFO, "DISKQUEUE(%s): readOne() opened %s", d.name, curFileName) //当前队列头在当前读取文件的位置 if d.readPos > 0 { _, err = d.readFile.Seek(d.readPos, 0) if err != nil { d.readFile.Close() d.readFile = nil return nil, err } } d.reader = bufio.NewReader(d.readFile) } // 先读取出消息的大小 err = binary.Read(d.reader, binary.BigEndian, &msgSize) if err != nil { d.readFile.Close() d.readFile = nil return nil, err } //判断消息大小是否合法 if msgSize < d.minMsgSize || msgSize > d.maxMsgSize { // this file is corrupt and we have no reasonable guarantee on // where a new message should begin d.readFile.Close() d.readFile = nil return nil, fmt.Errorf("invalid message read size (%d)", msgSize) } //读取消息 readBuf := make([]byte, msgSize) _, err = io.ReadFull(d.reader, readBuf) if err != nil { d.readFile.Close() d.readFile = nil return nil, err } totalBytes := int64(4 + msgSize) //将下一个要读取的位置往后移 d.nextReadPos = d.readPos + totalBytes d.nextReadFileNum = d.readFileNum //判断下一个读取的位置是不是超过了文件大小 if d.nextReadPos > d.maxBytesPerFile { if d.readFile != nil { d.readFile.Close() d.readFile = nil } //如果超过了，则当前队列头文件要往后移，且读取位置设置为0 d.nextReadFileNum++ d.nextReadPos = 0 } return readBuf, nil
} // 刷新缓存到磁盘
func (d *diskQueue) sync() error { if d.writeFile != nil { // 将缓冲区的数据从内存中拷贝刷新到硬盘中保存 err := d.writeFile.Sync() if err != nil { d.writeFile.Close() d.writeFile = nil return err } } //保存元数据 err := d.persistMetaData() if err != nil { return err } d.needSync = false return nil
} 五、ioLoop循环 这个函数是一个“守护”协程， 暴露的d.writeChan和d.readChan 如果外部有网writeChan里写数据在这里处理 同时，这里的消息也会通过d.readChan将消息不断的从队列中往外推 func (d *diskQueue) ioLoop() { var dataRead []byte var err error var count int64 var r chan []byte //开启一个timer，syncTimeout在系统配置里，默认是2秒一次 syncTicker := time.NewTicker(d.syncTimeout) for { // SyncEvery是系统配置，默认值是2500 // 也就是如果现在这段代码中的count的值如果到了2500,则将缓存中的数据保存到磁盘 if count == d.syncEvery { d.needSync = true } // needSync这个字段如果为true，则将缓存中的数据保存到磁盘 if d.needSync { err = d.sync() if err != nil { d.logf(ERROR, "DISKQUEUE(%s) failed to sync - %s", d.name, err) } //同步缓存到磁盘成功，将count重置为0 count = 0 } //如果队列头和尾中间还有数据，则从头部读取数据 if (d.readFileNum < d.writeFileNum) || (d.readPos < d.writePos) { if d.nextReadPos == d.readPos { dataRead, err = d.readOne() if err != nil { d.logf(ERROR, "DISKQUEUE(%s) reading at %d of %s - %s", d.name, d.readPos, d.fileName(d.readFileNum), err) d.handleReadError() continue } } //这里读取的数据放到readChan这个通道里 r = d.readChan } else { r = nil } select { // the Go channel spec dictates that nil channel operations (read or write) // in a select are skipped, we set r to d.readChan only when there is data to read //看上面的英文注释，如果r为空，则这个分支会被跳过，这是golang的一个特性 //将读取的消息，丢到d.readChan里面，r.readChan向外部暴露 case r <- dataRead: count++ // moveForward sets needSync flag if a file is removed //moveForward()会删除已经没用的file，这个文件中的数据已经全部被读取了，不在队列头和尾之间了 d.moveForward() case <-d.emptyChan: d.emptyResponseChan <- d.deleteAllFiles() count = 0 case dataWrite := <-d.writeChan: //如果d.writeChan有写入数据，则将消息数据写入到队列 count++ d.writeResponseChan <- d.writeOne(dataWrite) case <-syncTicker.C: //这里相当于两秒钟同步一次缓存到磁盘 if count == 0 { // avoid sync when there's no activity continue } d.needSync = true case <-d.exitChan: goto exit } } exit: d.logf(INFO, "DISKQUEUE(%s): closing ... ioLoop", d.name) syncTicker.Stop() d.exitSyncChan <- 1
}

